DROP TABLE IF EXISTS `sl_recommended_book`;
CREATE TABLE `sl_recommended_book` (
  `bookName` varchar(255) DEFAULT '' COMMENT '书名',
  `bookAuthor` varchar(255) DEFAULT '' COMMENT '作者',
  `bookISBN` varchar(20) NOT NULL DEFAULT '' COMMENT 'ISBN编号',
  `bookPublisher` varchar(255) DEFAULT '' COMMENT '出版商',
  `bookPublishDate` date DEFAULT '0000-00-00' COMMENT '出版时间',
  `bookLanguage` varchar(10) DEFAULT '中文' COMMENT '语言',
  `bookPrice` double(7,2) DEFAULT '0.00' COMMENT '价格RMB',
  `bookClass` varchar(10) DEFAULT '' COMMENT '类别',
  `bookContributor` varchar(50) DEFAULT '公司采购' COMMENT '贡献者',
  `bookIntro` text COMMENT '简介',
  `bookPicUrl` varchar(255) DEFAULT '/upload/picture/nopic.jpg' COMMENT '图片URL',
  `recUserName` varchar(255) DEFAULT '' COMMENT '推荐人姓名',
  `recUserEmail` varchar(255) DEFAULT NULL COMMENT '推荐人邮箱',
  `recStatus` varchar(4) DEFAULT '已推荐' COMMENT '推荐状态：已选购、已忽略、已推荐',
  `recDate` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '推荐日期',
  `recRate` int(4) NOT NULL DEFAULT '1' COMMENT '推荐度',
  `countPrice` double(7,2) DEFAULT '0.00' COMMENT '实际价格',
  PRIMARY KEY (`bookISBN`),
  KEY `recUserEmail` (`recUserEmail`),
  KEY `bookName` (`bookName`),
  KEY `bookAuthor` (`bookAuthor`),
  KEY `bookClass` (`bookClass`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='推荐书目';

